var form=document.getElementById("form"),firstName=document.getElementById("firstName"),lastName=document.getElementById("lastName"),email=document.getElementById("email"),password=document.getElementById("password");function showError(e,t){var a=e.parentElement;a.classList.add("error"),a.querySelector("small").innerText=t,email.classList.add("input-email"),email.placeholder="email@example/com"}function showSucces(e){var t=e.parentElement;t.classList.remove("error"),t.querySelector("small").innerText=""}function checkRequired(e){e.map(function(e){""===e.value.trim()?showError(e,e.name+" cannot be empty"):showSucces(e)})}function checkEmail(e){/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e.value.trim())?showSucces(e):showError(e,"Looks like this is not an email")}form.addEventListener("submit",function(e){e.preventDefault(),checkRequired([firstName,lastName,email,password]),checkEmail(email)});